              </TouchableOpacity>
            </View>
        )
        ToastAndroid.show(
          "Welcome, guest!",
          ToastAndroid.SHORT
        )
        props.navigation.navigate("HomeMap")
      }, 3000);



  }
  catch(exception) {
      return false;
  }
}

